var $ = jQuery.noConflict();

$(document).ready(function() {
    var table;
    table = $("#nilai-caddy").DataTable({
        processing: true, //Feature control the processing indicator.
        serverSide: true, //Feature control DataTables' server-side processing mode.
        order: [], //Initial no order.

        // Load data for the table's content from an Ajax source
        ajax: {
            url: rootWebService +
                "CaddyMaster/TampilDataDetailNilai/" +
                bulan +
                "/" +
                tahun +
                "/" +
                nip,
            type: "POST",
        },

        //Set column definition initialisation properties.
        columnDefs: [{
                targets: [-1], //last column
                orderable: false, //set not orderable
            },
            {
                targets: 3,
                orderable: false, //set not orderable
            },
            {
                targets: 4,
                orderable: false, //set not orderable
            },
            {
                targets: 0,
                className: "text-center",
            },
            {
                targets: 2,
                className: "text-center",
            },
            {
                targets: 3,
                className: "text-center",
            },
            {
                targets: 4,
                className: "text-center",
            },
            {
                targets: 5,
                className: "text-center",
            },
        ],
    });

    $("#show_data").on("change", ".nilai", function() {
        this.setAttribute("value", this.value);
        var values_nilai = $(this).attr("value");
        var id_nilai = $(this).attr("data-absen");
        var tipe = $(this).attr("data-tipe");
        $.ajax({
            type: "POST",
            url: rootWebService + "CaddyMaster/UpdateNilai",
            dataType: "JSON",
            data: {
                nilai: values_nilai,
                id_nilai: id_nilai,
                tipe: tipe,
            },
            success: function(data) {
                var msg = data.msg;
                if (msg === "SUCCESS") {} else if (msg === "FAIL") {
                    Notiflix.Report.Warning(
                        "Terjadi Kesalahan !",
                        "Tidak dapat terhubung ke database, coba beberapa saat lagi <br><br> - System",
                        "Tutup"
                    );
                }
            }, //success
            error: function(xhr, ajaxOption, thrownError) {
                Notiflix.Report.Failure(
                    "Terjadi Kesalahan !",
                    "Server tidak merespon permintaan !! - System<br>",
                    "Tutup"
                );
            }, //error
        });
    });

    //FUNCTION Selesai
    $("#show_data").on("click", ".selesai", function() {
        var id_nilai = $(this).attr("data-absen");
        var tanggal = tahun + "-" + bulan;
        Notiflix.Confirm.Show(
            "Konfrimasi",
            "Anda yakin penilaian sudah benar? Tindakan ini akan membuat penilaian tidak dapat diubah kembali !",
            "Ya",
            "Batal",
            function() {
                $.ajax({
                    type: "POST",
                    url: rootWebService + "CaddyMaster/UpdateNilaiSelesai",
                    dataType: "JSON",
                    data: { id_absen: id_nilai, periode: tanggal },
                    success: function(data) {
                        var msg = data.msg;
                        if (msg === "SUCCESS") {
                            Notiflix.Notify.Success("Penilaian Selesai !");
                            table.ajax.reload(null, false); //reload datatable ajax
                        } else if (msg === "FAIL") {
                            Notiflix.Report.Warning(
                                "Terjadi Kesalahan !",
                                "Tidak dapat terhubung ke database, coba beberapa saat lagi <br><br> - System",
                                "Tutup"
                            );
                        }
                    }, //success
                    error: function(xhr, ajaxOption, thrownError) {
                        Notiflix.Report.Failure(
                            "Terjadi Kesalahan !",
                            "Server tidak merespon permintaan !! - System<br>",
                            "Tutup"
                        );
                    }, //error
                });
            },
            false
        );
    });
});