var $ = jQuery.noConflict();

$(document).ready(function() {
    var table;
    var bulan = $("select[name=bulan] option").filter(":selected").val();
    var tahun = $("select[name=tahun] option").filter(":selected").val();
    table = $("#detail-absen-caddy").DataTable({
        processing: true, //Feature control the processing indicator.
        serverSide: true, //Feature control DataTables' server-side processing mode.
        order: [], //Initial no order.

        // Load data for the table's content from an Ajax source
        ajax: {
            url: rootWebService +
                "CaddyMaster/FilterAbsen/" +
                fp +
                "/" +
                tahun +
                "-" +
                bulan,
            type: "POST",
        },

        //Set column definition initialisation properties.
        columnDefs: [{
                targets: [-1], //last column
                orderable: false, //set not orderable
            },
            {
                targets: 0,
                className: "text-center",
            },
            {
                targets: 2,
                className: "text-center",
            },
            {
                targets: 3,
                className: "text-center",
            },
            {
                targets: 4,
                className: "text-center",
            },
        ],
    });

    function reload_table(tahun, bulan) {
        $.ajax({
            type: "POST",
            url: rootWebService +
                "CaddyMaster/DataDetailAbsen/" +
                fp +
                "/" +
                tahun +
                "-" +
                bulan,
            dataType: "JSON",
            success: function(data) {
                var data_absen = data.total_absen;
                var data_sakit = data.total_sakit;
                $("#total-absen").html(data_absen);
                $("#total-sakit").html(data_sakit);
            }, //success
            error: function(xhr, ajaxOption, thrownError) {
                Notiflix.Report.Failure(
                    "Terjadi Kesalahan !",
                    "Server tidak merespon permintaan !! - System<br>",
                    "Tutup"
                );
            }, //error
        });
        table.ajax.reload(null, false); //reload datatable ajax
    }

    $("#BulanAbsen").on("keyup change", function() {
        var bulan_filter = $("select[name=bulan] option").filter(":selected").val();
        var tahun_filter = $("select[name=tahun] option").filter(":selected").val();
        reload_table(tahun_filter, bulan_filter);
    });

    $("#TahunAbsen").on("keyup change", function() {
        var bulan_filter = $("select[name=bulan] option").filter(":selected").val();
        var tahun_filter = $("select[name=tahun] option").filter(":selected").val();
        reload_table(tahun_filter, bulan_filter);
    });

    //FUNCTION Hapus Absen
    $("#show_data").on("click", ".hapus-absen", function() {
        var id = $(this).attr("data");
        var data_nama = $(this).attr("data-nama");
        Notiflix.Confirm.Show(
            "Konfrimasi",
            "Anda Yakin ingin menghapus Absen ?",
            "Ya",
            "Batal",
            function() {
                $.ajax({
                    type: "POST",
                    url: rootWebService + "CaddyMaster/DeleteAbsenSakit/" + id,
                    dataType: "JSON",
                    success: function(data) {
                        var msg = data.msg;
                        if (msg === "SUCCESS") {
                            Notiflix.Notify.Success("Berhasil menghapus Absen !");
                            reload_table();
                        } else if (msg === "FAIL") {
                            Notiflix.Report.Warning(
                                "Terjadi Kesalahan !",
                                "Tidak dapat terhubung ke database, coba beberapa saat lagi <br><br> - System",
                                "Tutup"
                            );
                        }
                    }, //success
                    error: function(xhr, ajaxOption, thrownError) {
                        Notiflix.Report.Failure(
                            "Terjadi Kesalahan !",
                            "Server tidak merespon permintaan !! - System<br>",
                            "Tutup"
                        );
                    }, //error
                });
            },
            false
        );
    });
});