var $ = jQuery.noConflict();

$(document).ready(function() {
    var table;
    table = $("#table-laporan").DataTable({
        processing: true, //Feature control the processing indicator.
        serverSide: true, //Feature control DataTables' server-side processing mode.
        order: [], //Initial no order.

        // Load data for the table's content from an Ajax source
        ajax: {
            url: rootWebService +
                "Manager/TampilDataDetailLaporanMenunggu/" +
                bulan +
                "/" +
                tahun,
            type: "POST",
        },

        //Set column definition initialisation properties.
        columnDefs: [{
                targets: [-1], //last column
                orderable: false, //set not orderable
            },
            {
                targets: 2,
                orderable: false, //set not orderable
            },
            {
                targets: 0,
                className: "text-center",
            },
            {
                targets: 1,
                className: "text-center",
            },
            {
                targets: 3,
                className: "text-center",
            },
            {
                targets: 4,
                className: "text-center",
            },
            {
                targets: 5,
                className: "text-center",
            },
            {
                targets: 6,
                className: "text-center",
            },
            {
                targets: 7,
                className: "text-center",
            },
        ],
    });

    //FUNCTION Selesai
    $("#show_data").on("click", ".selesai", function() {
        var id_lap = $(this).attr("data-laporan");
        var periode = $(this).attr("data-periode");
        var nama = $(this).attr("data-nama");
        Notiflix.Confirm.Show(
            "Konfrimasi",
            "Konfrimasi Laporan " + nama + " ?",
            "Ya",
            "Batal",
            function() {
                $.ajax({
                    type: "POST",
                    url: rootWebService + "Manager/UpdateLaporanSelesai",
                    dataType: "JSON",
                    data: { id_laporan: id_lap, periode: periode },
                    success: function(data) {
                        var msg = data.msg;
                        if (msg === "SUCCESS") {
                            Notiflix.Notify.Success("Laporan " + nama + " Dikonfrimasi !");
                            table.ajax.reload(null, false); //reload datatable ajax
                        } else if (msg === "FAIL") {
                            Notiflix.Report.Warning(
                                "Terjadi Kesalahan !",
                                "Tidak dapat terhubung ke database, coba beberapa saat lagi <br><br> - System",
                                "Tutup"
                            );
                        }
                    }, //success
                    error: function(xhr, ajaxOption, thrownError) {
                        Notiflix.Report.Failure(
                            "Terjadi Kesalahan !",
                            "Server tidak merespon permintaan !! - System<br>",
                            "Tutup"
                        );
                    }, //error
                });
            },
            false
        );
    });
});